import BetState from "./BetState.vue";
import { setTimeout } from 'timers';
var ROW_INFO_HEIGHT = 124;
var ROW_BET_0_HEIGHT = 156;
var ROW_BET_1_HEIGHT = 178;
var ROW_BET_2_HEIGHT = 178;
var ROW_BET_3_HEIGHT = 110;
var ROW_BET_4_HEIGHT = 40;
var BORDER_HEIGHT = 20;
var BASE_HEIGHT = ROW_INFO_HEIGHT + ROW_BET_0_HEIGHT + ROW_BET_1_HEIGHT + ROW_BET_2_HEIGHT + ROW_BET_3_HEIGHT + ROW_BET_4_HEIGHT + BORDER_HEIGHT;
var MAX_BET_PER_GAME = 50000000;
export default {
  data: function data() {
    return {
      rowInfo: 0,
      // info row height
      rowBet0: 0,
      // 1st bet row height
      rowBet1: 0,
      // 2nd bet row height
      rowBet2: 0,
      // 3th bet row height
      rowBet3: 0,
      // 4th bet row height
      rowBet4: 0,
      // dividend row height
      classLeftCard: 'card',
      // 첫 번째 화투의 css class
      classRightCard: 'card',
      // 두 번째 화투의 css class
      classLeftFlipCard: 'fcard',
      // 첫 번째 화투의 flip 애니메이션 css class
      classRightFlipCard: 'fcard',
      // 두 번째 화투의 flip 애니메이션 css class
      classTong: 'tong',
      // 식보통 css class
      classPad: 'pad',
      timerString: '--:--',
      // 배팅 타이머
      statusString: 'new_game',
      // 배팅 상태 이름
      classBettingCells: ['', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''],
      // 당첨된 포지션을 표시하는 css class
      imgCard1: '/img/sicbo/card/00.svg',
      // 첫 번째 카드 이미지
      imgCard2: '/img/sicbo/card/00.svg',
      // 두 번째 카드 이미지
      classStatus: 'bg-status'
    };
  },
  props: {
    width: {
      // 게임판의 너비
      type: Number,
      default: 0
    },
    height: {
      // 게임판의 높이
      type: Number,
      default: 0
    },
    bets: {
      // 배팅 데이터
      type: Array,
      default: function _default() {
        return [];
      }
    },
    chipselected: {
      // 칩 선택 여부
      type: Boolean,
      default: false
    },
    hits: {
      // 담청 포지션 정보
      type: Array,
      default: function _default() {
        return [];
      }
    },
    lastresult: {
      // 마지막 게임 정보
      type: Object,
      default: function _default() {
        return {
          cards: [0, 0],
          pair: false,
          small: false,
          odd: false
        };
      }
    },
    isMyBetEmpty: {
      // mybet이 비었는지 여부
      type: Boolean,
      default: function _default() {
        return true;
      }
    }
  },
  watch: {
    /**
     * 게임판 높이 변경에 따른 배팅 셀의 높이 변경
     */
    height: function height(_height) {
      var base = ROW_INFO_HEIGHT + ROW_BET_0_HEIGHT + ROW_BET_1_HEIGHT + ROW_BET_2_HEIGHT + ROW_BET_3_HEIGHT + ROW_BET_4_HEIGHT;
      this.rowBet0 = Math.round((_height - BORDER_HEIGHT) * (ROW_BET_0_HEIGHT / base));
      this.rowBet1 = Math.round((_height - BORDER_HEIGHT) * (ROW_BET_1_HEIGHT / base));
      this.rowBet2 = Math.round((_height - BORDER_HEIGHT) * (ROW_BET_2_HEIGHT / base));
      this.rowBet3 = Math.round((_height - BORDER_HEIGHT) * (ROW_BET_3_HEIGHT / base));
      this.rowBet4 = Math.round((_height - BORDER_HEIGHT) * (ROW_BET_4_HEIGHT / base));
      this.rowInfo = _height - (this.rowBet0 + this.rowBet1 + this.rowBet2 + this.rowBet3 + this.rowBet4 + BORDER_HEIGHT);
    },

    /**
     * 담청 포지션 애니메이션 출력
     */
    hits: function hits(_hits) {
      var cells = ['', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ''];

      for (var idx = 0; idx < _hits.length; idx++) {
        cells[_hits[idx]] = 'twinkle';
      }

      this.classBettingCells = cells;
    }
  },
  computed: {
    /**
     * 마지막 게임의 첫 번째 카드
     */
    lastResultCard1: function lastResultCard1() {
      if (this.lastresult.cards[0] === -1) {
        return '/img/sicbo/card/back.svg';
      } else if (this.lastresult.cards[0] < 10) {
        return "/img/sicbo/card/0".concat(this.lastresult.cards[0], ".svg");
      }

      return "/img/sicbo/card/".concat(this.lastresult.cards[0], ".svg");
    },

    /**
     * 마지막 게임의 두 번째 카드
     */
    lastResultCard2: function lastResultCard2() {
      if (this.lastresult.cards[0] === -1) {
        return '/img/sicbo/card/back.svg';
      } else if (this.lastresult.cards[1] < 10) {
        return "/img/sicbo/card/0".concat(this.lastresult.cards[1], ".svg");
      }

      return "/img/sicbo/card/".concat(this.lastresult.cards[1], ".svg");
    },

    /**
     * 게임판의 너비와 높이를 지정하는 style
     */
    styleLayout: function styleLayout() {
      return "width:".concat(this.width, "px;height:").concat(this.height, "px");
    },

    /**
     * 최대 배팅 가능액에 대한 현재 배팅액의 비율
     */
    currentBetRate: function currentBetRate() {
      var total = this.bets.reduce(function (total, bet) {
        return total + bet.amount;
      }, 0);
      return total / MAX_BET_PER_GAME * 100;
    },

    /**
     * 게임판 높이에 따라 폰트 사이즈를 변경
     */
    fontSizeTitle1: function fontSizeTitle1() {
      return 18 * this.height / BASE_HEIGHT;
    },
    fontSizeTimer: function fontSizeTimer() {
      if (this.width <= 1200) return 40 * this.height / BASE_HEIGHT;
      return 48 * this.height / BASE_HEIGHT;
    },
    fontSizeStatus: function fontSizeStatus() {
      if (this.width <= 1200) return 16 * this.height / BASE_HEIGHT;
      return 26 * this.height / BASE_HEIGHT;
    },
    fontSizeRange: function fontSizeRange() {
      return 25 * this.height / BASE_HEIGHT;
    },
    fontSizeName: function fontSizeName() {
      return 64 * this.height / BASE_HEIGHT;
    },
    fontSizeNumber: function fontSizeNumber() {
      return 48 * this.height / BASE_HEIGHT;
    },
    fontSizeGuang: function fontSizeGuang() {
      return 96 * this.height / BASE_HEIGHT;
    },
    fontSizeDividend: function fontSizeDividend() {
      return 18 * this.height / BASE_HEIGHT;
    },

    /**
     * 게임판 높이에 따라 화투 패드의 크기 변경
     */
    padSizeDesc: function padSizeDesc() {
      if (this.width <= 1200) return 2 * this.height / BASE_HEIGHT;
      return 8 * this.height / BASE_HEIGHT;
    },
    padWidth: function padWidth() {
      return this.width * 0.2;
    },
    padHeight: function padHeight() {
      return this.height * 0.35;
    },
    cardWidth: function cardWidth() {
      return this.width * 0.2 * 0.2;
    }
  },
  methods: {
    /**
     * 포지션의 배팅 금액
     * 
     * @param id 포지션 ID
     */
    betAmount: function betAmount(id) {
      return this.bets[id].amount;
    },

    /**
     * 첫 번째 화투 투척 애니메이션
     */
    cast1stCard: function cast1stCard() {
      this.classLeftCard = 'card cast-to-left';
    },

    /**
     * 두 번째 화투 애니메이션
     */
    cast2ndCard: function cast2ndCard() {
      this.classRightCard = 'card cast-to-right';
    },

    /**
     * 식보통을 덮는 애니메이션
     */
    closeTong: function closeTong() {
      this.classTong = "tong close";
    },

    /**
     * 식보통을 여는 애니메이션
     */
    openTong: function openTong() {
      this.classTong = "tong";
    },

    /**
     * 첫 번째 화투를 오픈하는 애니메이션
     */
    open1stCard: function open1stCard(num) {
      if (num < 10) {
        this.imgCard1 = "/img/sicbo/card/0".concat(num, ".svg");
      } else {
        this.imgCard1 = "/img/sicbo/card/".concat(num, ".svg");
      }

      this.classLeftFlipCard = 'fcard is-flipped';
    },

    /**
     * 두 번째 화투를 오픈하는 애니메이션
     */
    open2ndCard: function open2ndCard(num) {
      if (num < 10) {
        this.imgCard2 = "/img/sicbo/card/0".concat(num, ".svg");
      } else {
        this.imgCard2 = "/img/sicbo/card/".concat(num, ".svg");
      }

      this.classRightFlipCard = 'fcard is-flipped';
    },

    /**
     * 게임을 정리
     */
    stopGame: function stopGame() {
      this.classLeftCard = 'card';
      this.classRightCard = 'card';
      this.classLeftFlipCard = 'fcard';
      this.classRightFlipCard = 'fcard';
      this.classTong = 'tong';
    },

    /**
     * 배팅 타이머
     */
    setTimerString: function setTimerString(betTimer) {
      this.timerString = betTimer;
    },

    /**
     * 게임 상태 변경
     */
    setStatus: function setStatus(status) {
      var _this = this;

      if (this.statusString !== status) {
        this.classStatus = 'bg-status twinkle2';
        setTimeout(function () {
          _this.classStatus = 'bg-status';
        }, 3000);
        this.statusString = status;
      }
    },

    /**
     * 배팅
     * 
     * @param id 포지션 ID
     * @param $event 터치 이벤트 데이터
     */
    bet: function bet(id, event) {
      this.$emit('bet', {
        id: id,
        tableType: 'pc',
        x: event.clientX,
        y: event.clientY
      });
    },

    /**
     * 배팅을 취소
     */
    cancelBet: function cancelBet(id) {
      this.$emit('cancel', id);
    }
  },
  components: {
    'g-bet-state': BetState
  }
};
